<?phpif( ! defined( 'WP_UNINSTALL_PLUGIN' ) ) exit();$posts = get_posts('numberposts=-1&post_type=campaigns&post_status=any' );foreach ($posts as $post) {	wp_delete_post($post->ID, true);	delete_post_meta( $post->ID, 'start_date');	delete_post_meta( $post->ID, 'end_date');	delete_post_meta( $post->ID, 'candidates');	delete_post_meta( $post->ID, 'camp_voter_name');	delete_post_meta( $post->ID, 'camp_voter_email');	delete_post_meta( $post->ID, 'camp_voter_phone');}?>