<?phpif( ! defined( 'WP_UNINSTALL_PLUGIN' ) ) exit();// Deleting Posts$posts = get_posts('numberposts=-1&post_type=v_job_board&post_status=any' );foreach ($posts as $post) {	wp_delete_post($post->ID, true);	delete_post_meta( $post->ID, 'salary_range');	delete_post_meta( $post->ID, 'location');	delete_post_meta( $post->ID, 'total_jobs_link');	delete_post_meta( $post->ID, 'cv_library_link');	delete_post_meta( $post->ID, 'indeed_link');}// Deleting Tableglobal $wpdb;$table_name = $wpdb->prefix . 'ea_apply';$sql = "DROP TABLE IF EXISTS $table_name";$wpdb->query($sql);?>